package org.opendaylight.genius.itm.validator;import org.opendaylight.genius.itm.globals.ITMConstants;import org.opendaylight.yang.gen.v1.urn.opendaylight.genius.itm.rev160406.transport.zones.TransportZone;/** * Created by sathishkumar.bt on 06-09-2016. */public class TransportZoneAllowed implements TransportZoneValidator {    @Override    public void validate(TransportZone zone) {        if(zone.getZoneName().equalsIgnoreCase(ITMConstants.DEFAULT_TRANSPORT_ZONE)){           throw new IllegalArgumentException("Listener for "+ITMConstants.DEFAULT_TRANSPORT_ZONE+ " are not processed");        }    }}